import { SocialAuthService, SocialUser } from '@abacritt/angularx-social-login';
import { Component, OnInit, ViewEncapsulation } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service'; 
import { UserService } from 'src/app/services/user.service'; 

@Component({
  selector: 'app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.scss'],
  encapsulation: ViewEncapsulation.None
})
export class UserLoginComponent implements OnInit{
  loginForm: FormGroup;
  errorMessage: String = '';

  showWelcomeMessage = false;
  showAdminError = false;
  showAuthError = false;
  welcomeEmail = '';
  isLoading=false;

  showResetPasswordModal = false;
  resetEmail = '';
  resetPasswordSuccess = false;
  resetPasswordError = '';


  showCodeVerificationModal = false;
showPasswordChangedModal = false;
verificationEmail = '';
verificationCode = '';
newPassword = '';
codeVerificationError = '';
socialUser!: SocialUser;
isLoggedin?: boolean;

  SignInOptions = [
    {
      image: 'assets/images/authentication/google.svg',
      name: 'Google'
    },
    {
      image: 'assets/images/authentication/twitter.svg',
      name: 'Twitter'
    },
    {
      image: 'assets/images/authentication/facebook.svg',
      name: 'Facebook'
    }
  ];


  constructor(
    private fb: FormBuilder,
    private userService: UserService,
    private authService:AuthService,
    private socialAuthService: SocialAuthService,

    private router: Router
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });


  }


  ngOnInit() {
   // localStorage.clear();
    this.socialAuthService.authState.subscribe((user) => {
      this.socialUser = user;
      this.isLoggedin = user != null;
      if (this.isLoggedin) {
        this.handleSocialLogin(user);
      }
    });
  }
  private handleSocialLogin(user: SocialUser) {
    this.userService.registerWithGoogle(user).subscribe({
      next: (response: any) => {
        if (response.token) {
          localStorage.setItem('auth_token', response.token);
          this.authService.setAuthToken(response.token);
          this.router.navigate(['user/home']);
        }
      },
      error: (err) => {
        console.error('Erreur lors de la connexion avec Google', err);
      }
    });
  }

