import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from '../user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-admin',
  templateUrl: './login-admin.component.html',
  styleUrls: ['./login-admin.component.css']
})
export class LoginAdminComponent {
  loginForm: FormGroup;
  errorMessage: String = '';

  constructor(
    private fb: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });


  }
onSubmit() {
  this.errorMessage = ''; // Réinitialise le message d'erreur

  if (this.loginForm.valid) {

    const { email, password } = this.loginForm.value;

    this.userService.login({ email, password }).subscribe({
      next: (response) => {
        if (response.status === 'success' && response.token) {
          console.log('Token reçu:', response.token);
          localStorage.setItem('auth_token', response.token);
          this.router.navigate(['http://localhost:4200/'])        } else {
          this.errorMessage = response.message || 'Authentification échouée';
        }
      },
      error: (err) => {
        console.error('Erreur complète:', err);
        this.errorMessage = err.error?.message || 'Erreur de connexion au serveur';
      }
    });
  }
}
redirecttodashboard(){

  this.router.navigate(['admin/user-admin']);
}

  handleImageError(event: Event) {
    const img = event.target as HTMLImageElement;
    console.error('Image non trouvée:', img.src);
    img.style.border = '2px solid red';
  }


}
