import { Component, OnDestroy, OnInit } from '@angular/core';
import { Event, NavigationEnd, Router } from '@angular/router';
import { DashboardService } from '../dashboard.service';
import { Subscription } from 'rxjs';
import { AuthService } from 'src/app/auth.service';

@Component({
  selector: 'dashboard-layout',
  templateUrl: './layout.component.html',
  styleUrls: ['./layout.component.css'],
})
export class LayoutComponent implements OnInit , OnDestroy {
  isAuthenticated = false;
  private authSubscription!: Subscription; // Pour gérer la désouscription

  constructor(
    private router: Router,
    private dashboard: DashboardService,
    private authService: AuthService // Injection du service
  ) {
    // Gestion des événements de route (existant)
    this.router.events.subscribe((event: Event) => {
      this.dashboard.setCurrentRoute(this.router.url);
      if (event instanceof NavigationEnd) {
        if (this.dashboard.sidebarOpen && window.innerWidth < 1024) {
          this.dashboard.closeSidebar();
        }
      }
    });
  }

  ngOnInit() {
    document.documentElement.style.overflow = 'hidden';

    // Souscription à l'état d'authentification
    this.authSubscription = this.authService.isAuthenticated$.subscribe(
      (auth) => {
        this.isAuthenticated = auth;

        // Rediriger vers /login si non authentifié (optionnel)
        if (!auth && !this.router.url.includes('login')) {
          this.router.navigate(['/login']);
        }
      }
    );
  }

  ngOnDestroy() {
    // Nettoyage de la souscription
    this.authSubscription?.unsubscribe();
    document.documentElement.style.overflow = '';
  }
}
