import { Component, HostListener } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service'; 
import { UserService } from 'src/app/services/user.service'; 

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  isAuthenticated: boolean = false; // Variable simple (pas d'Observable)
  userInfo: any;
  constructor(private router: Router,  private authService: AuthService,private userService: UserService,

  ) { 

      this.isAuthenticated = this.authService.isAuthenticatedSync();
}


isProfileDropdownOpen = false;
isUserDetailsModalOpen = false;
currentUser: any;

toggleProfileDropdown(event: Event): void {
  this.currentUser = this.authService.getCurrentUserInfo();
  console.log('User info frontttt:', this.currentUser);
  event.stopPropagation();
  this.isProfileDropdownOpen = !this.isProfileDropdownOpen;
}

openUserDetailsModal(): void {
  this.currentUser = this.authService.getCurrentUserInfo();
  this.isUserDetailsModalOpen = true;
  this.isProfileDropdownOpen = false;
}

closeUserDetailsModal(): void {
  this.isUserDetailsModalOpen = false;
}

@HostListener('document:click', ['$event'])
closeProfileDropdown(event: Event): void {
  if (!(event.target as HTMLElement).closest('#user-menu-button') && 
      !(event.target as HTMLElement).closest('#profile-dropdown')) {
    this.isProfileDropdownOpen = false;
  }
}

logout(): void {
  this.authService.logout();
  this.isProfileDropdownOpen = false;
}

}
<nav class="navbar navbar-expand-lg navbar-light bg-white shadow-sm">
  <div class="container-fluid">
    <a class="navbar-brand d-flex align-items-center" href="/">
      <img src="assets/skillswap.png" alt="Skillswap Logo" width="100" class="" class="me-3" />
      <span class="fw-bold text-primary">Skillswap</span>
    </a>

    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavDropdown"
      aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

      <ul class="navbar-nav ms-auto align-items-lg-center">
        <li class="nav-item">
          <a class="nav-link" href="/"><i class="fas fa-home me-1"></i> Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="/about"><i class="fas fa-info-circle me-1"></i> About Us</a>
        </li>

        <li class="nav-item">
          <a class="nav-link" href="/contact"><i class="fas fa-phone-alt me-1"></i> Contact</a>
        </li>

        <!-- Dropdown menu for management -->
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" id="managementDropdown" role="button" data-bs-toggle="dropdown"
            aria-expanded="false">
            <i class="fas fa-cogs me-1"></i> Management
          </a>
          <ul class="dropdown-menu" aria-labelledby="managementDropdown">

            <li><a class="dropdown-item" routerLink="/student/eventf"><i class="fas fa-calendar me-2"></i> Events</a></li>
            <li><a class="dropdown-item" href="/reservations"><i class="fas fa-bookmark me-2"></i> My Reservations</a></li>
            <li><a class="dropdown-item" href="/student/courses" ><i class="fas fa-graduation-cap me-2"></i> Course</a></li>
            <li><a class="dropdown-item" href="/professor/courses" ><i class="fas fa-graduation-cap me-2"></i> Course</a></li>

            <li><a class="dropdown-item" href="/forum"><i class="fas fa-comments me-2"></i> Forum</a></li>
            <li><a class="dropdown-item" href="/messaging"><i class="fas fa-envelope me-2"></i> Messaging</a></li>
            <li><a class="dropdown-item" href="/complaint"><i class="fas fa-exclamation-circle me-2"></i> Complaint</a></li>
          </ul>
        </li>
        <li class="nav-item dropdown" [class.show]="isProfileDropdownOpen">
          <a class="nav-link dropdown-toggle" href="#" id="profileDropdown" role="button" 
             (click)="toggleProfileDropdown($event)"
             aria-expanded="isProfileDropdownOpen">
            <i class="fas fa-user me-1"></i> Profil
          </a>
          <ul class="dropdown-menu" [class.show]="isProfileDropdownOpen" aria-labelledby="profileDropdown">
            <li>
              <a class="dropdown-item" (click)="openUserDetailsModal()">
                <i class="fas fa-eye me-2"></i> View Details
              </a>
            </li>
            <li>
              <a class="dropdown-item" (click)="logout()">
                <i class="fas fa-sign-out-alt me-2"></i> Logout
              </a>
            </li>
          </ul>

        </li>
        <li>
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        </li>
      </ul>
    </div>
</nav>
