export class CourseListBComponent {
  constructor(courseService, authService, router, categoryService, route) {
    this.courseService = courseService;
    this.authService = authService;
    this.router = router;
    this.categoryService = categoryService;
    this.route = route;
    this.courses = [];
    this.categories = [];
    this.loading = true;
    this.error = '';
    this.allCourses = [];
    this.filteredCourses = [];
    this.searchQuery = '';
    this.sortOption = '';
    this.currentPage = 1;
    this.itemsPerPage = 6;
    this.popularCourses = [];
    this.showOnlyMyCourses = false;
    this.staticStudentId = 2; // ID statique pour l'étudiant
    this.staticTeacherId = 1; // ID statique pour le professeur
    this.hoveredRating = 0;
    this.selectedRating = {};
    this.filterOnline = false;
    this.filterLive = false;
  }
  ngOnInit() {
    this.loadCategories();
    this.loadCourses();
    const courseId = this.route.snapshot.paramMap.get('id');
    const rating = this.route.snapshot.paramMap.get('rating');
    this.loadPopularCourses(); // Ajoutez cet appel
  }

  isCourseOwner(course) {
    return course.teacherId === this.staticTeacherId;
  }
  toggleCourseView() {
    this.showOnlyMyCourses = !this.showOnlyMyCourses;
    this.currentPage = 1;
  }
  getDisplayedCourses() {
    return this.showOnlyMyCourses ? this.filteredCourses.filter(course => this.isCourseOwner(course)) : this.filteredCourses;
  }
  getPaginatedCourses() {
    const start = (this.currentPage - 1) * this.itemsPerPage;
    return this.getDisplayedCourses().slice(start, start + this.itemsPerPage);
  }
  getPages() {
    const totalPages = Math.ceil(this.getDisplayedCourses().length / this.itemsPerPage);
    return Array.from({
      length: totalPages
    }, (_, i) => i + 1);
  }
  onManageContents(course) {
    this.courseService.incrementViewCount(course.id_Course).subscribe({
      next: () => {
        this.router.navigate(['/professor/courses', course.id_Course, 'contents']);
      },
      error: err => {
        console.error('Failed to open contents', err);
        this.router.navigate(['/professor/courses', course.id_Course, 'contents']);
      }
    });
  }
  loadCategories() {
    this.categoryService.getAllCategories().subscribe(categories => {
      this.categories = categories;
    }, error => {
      console.error('Erreur lors du chargement des catégories', error);
    });
  }
  loadCourses() {
    /* const currentUser = this.authService.getCurrentUser();
     if (!currentUser) {
       this.router.navigate(['/login']);
       return;
     }*/
    this.loading = true;
    this.courseService.getAllCourses().subscribe(courses => {
      this.allCourses = courses;
      this.filteredCourses = courses;
      this.loading = false;
    }, error => {
      console.error('Erreur lors du chargement des cours', error);
      this.error = 'Impossible de charger les cours. Veuillez réessayer plus tard.';
      this.loading = false;
    });
  }
  onSearch() {
    if (this.searchQuery.trim() === '') {
      this.filteredCourses = this.allCourses;
    } else {
      this.filteredCourses = this.allCourses.filter(course => course.title_Course.toLowerCase().includes(this.searchQuery.toLowerCase()) || course.description_Course.toLowerCase().includes(this.searchQuery.toLowerCase()));
    }
  }
  onSortChange() {
    if (this.sortOption === 'price') {
      this.filteredCourses.sort((a, b) => a.price_Course - b.price_Course);
    } else if (this.sortOption === 'duration') {
      this.filteredCourses.sort((a, b) => a.duration_Course - b.duration_Course);
    } else if (this.sortOption === 'rating') {
      this.filteredCourses.sort((a, b) => (b.rating_Course ?? 0) - (a.rating_Course ?? 0));
    }
  }
  filterByCategory(categoryName) {
    this.filteredCourses = this.allCourses.filter(course => course.name_Category === categoryName);
  }
  onEditCourse(id) {
    const course = this.allCourses.find(c => c.id_Course === id);
