import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { User } from './models/user';
import { HttpClient, HttpHeaders } from '@angular/common/http';


@Injectable({
  providedIn: 'root'
})
export class UserService {

  private apiServerUrl ="http://localhost:8089/skillswap";

  constructor(private http: HttpClient){}

  public getUsers(): Observable<User[]> {
    const headers =new HttpHeaders({
      'Content-Type': 'application/json',
     // 'Authorization': `Bearer ${token}`
    });

    return this.http.get<User[]>(`${this.apiServerUrl}/user/getall`,{headers});
  }

  public addUser(user: User): Observable<User> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
    //  'Authorization': 'Bearer your-token' 
    });
    return this.http.post<User>(`${this.apiServerUrl}/user/register`, user, { headers })
  }

  public updateEmployee(user: User): Observable<User> {
    return this.http.put<User>(`${this.apiServerUrl}update`, user);
  }

  public deleteUser(userId: number): Observable<void> {
    return this.http.delete<void>(`${this.apiServerUrl}delete/${userId}`);
  }
}
