import { Component, OnInit, OnDestroy } from '@angular/core';
import { Event, NavigationEnd, Router } from '@angular/router';
import { DashboardService } from '../dashboard.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'dashboard-layout',
  templateUrl: './layout.component.html',
  styleUrls: ['./layout.component.css'],
})
export class LayoutComponent implements OnInit, OnDestroy {
  private authSub!: Subscription;
  isAuthenticated: boolean = false;

  constructor(private router: Router, private dashboard: DashboardService) {
    this.router.events.subscribe((event: Event) => {
      this.dashboard.setCurrentRoute(this.router.url);
      if (event instanceof NavigationEnd) {
        if (this.dashboard.sidebarOpen && window.innerWidth < 1024) {
          this.dashboard.closeSidebar();
        }
      }
    });
  }

  ngOnInit() {
    // Dans n'importe quel composant/service
    this.checkAuthentication();
    document.documentElement.style.overflow = 'hidden';

    // Écoute les changements de route pour vérifier à nouveau l'authentification
    this.authSub = this.router.events.subscribe(event => {
      if (event instanceof NavigationEnd) {
        this.checkAuthentication();
      }
    });
  }

  ngOnDestroy() {
    if (this.authSub) {
      this.authSub.unsubscribe();
    }
  }

  checkAuthentication() {
    const token = localStorage.getItem('auth_token');
    this.isAuthenticated = !!token;
    console.log('Authentication status:', this.isAuthenticated); // Pour le débogage
  }
}
