import { Component, OnInit } from '@angular/core';
import { User } from '../models/user';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user-admin',
  templateUrl: './user-admin.component.html',
  styleUrls: ['./user-admin.component.css']
})
export class UserAdminComponent implements OnInit {
  users: User[] = [];
  filteredUsers: User[] = [];
  isLoading = true;
  errorMessage = '';
  selectedUser: User | null = null;

  // Variables pour la recherche multicritère
  searchQuery: string = '';
  selectedRole: string = '';
  selectedGender: string = '';
  selectedStatus: string = '';
  availableRoles: string[] = ['ADMIN','ETUDIANT'];
  availableGenders: string[] = ['Male', 'Female', 'Other'];

  constructor(private userService: UserService) {}

  ngOnInit(): void {
    this.loadUsers();
  }

  loadUsers(): void {
    this.isLoading = true;
    this.userService.getUsers().subscribe({
      next: (users) => {
        this.users = users;
        this.filteredUsers = [...users];
        this.isLoading = false;
        this.extractAvailableRoles();
        console.log('Liste des utilisateurs chargée :', this.users);
      },
      error: (err) => {
        this.errorMessage = 'Erreur lors du chargement des utilisateurs';
        this.isLoading = false;
        console.error(err);
      }
    });
  }

  private extractAvailableRoles(): void {
    this.availableRoles = [...new Set(this.users.map(user => user.role))].filter(role => role);
  }

  applyFilters(): void {
    this.filteredUsers = this.users.filter(user => {
      const matchesSearch = !this.searchQuery || 
        user.email?.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
        user.first_Name?.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
        user.last_Name?.toLowerCase().includes(this.searchQuery.toLowerCase());

      const matchesRole = !this.selectedRole || user.role === this.selectedRole;
      const matchesGender = !this.selectedGender || user.gender === this.selectedGender;
      const matchesStatus = !this.selectedStatus || 
        (this.selectedStatus === 'active' && user.is_Activated) ||
        (this.selectedStatus === 'inactive' && !user.is_Activated);

      return matchesSearch && matchesRole && matchesGender && matchesStatus;
    });
  }

  resetFilters(): void {
    this.searchQuery = '';
    this.selectedRole = '';
    this.selectedGender = '';
    this.selectedStatus = '';
    this.filteredUsers = [...this.users];
  }

  exportdata(): void {
    this.userService.exportUsersToExcel();
  }

  deleteUser(userId: number): void {
    if (confirm('Êtes-vous sûr de vouloir supprimer cet utilisateur ?')) {
      this.userService.deleteUser(userId).subscribe({
        next: () => {
          this.users = this.users.filter(user => user.id_User !== userId);
          this.filteredUsers = this.filteredUsers.filter(user => user.id_User !== userId);
        },
        error: (err) => {
          console.error('Erreur lors de la suppression', err);
        }
      });
    }
  }

  openDetailsModal(user: User): void {
    this.selectedUser = user;
  }

  closeDetailsModal(): void {
    this.selectedUser = null;
  }

  toggleUserStatus(user: User): void {

  }
}
